import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    console.error("Error handler:", err);
    res.status(status).json({ message });
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  // Check for demo authentication first
  if ((req.session as any)?.demoUser) {
    const demoUser = (req.session as any).demoUser;
    // Create a mock req.user structure for compatibility
    (req as any).user = {
      claims: {
        sub: demoUser.id,
        email: demoUser.email,
      },
      isAdmin: demoUser.isAdmin,
    };
    return next();
  }

  // Original Replit OIDC authentication logic
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { z } from "zod";
import { insertToolSchema, insertCategorySchema, insertDiscountCodeSchema } from "@shared/schema";

// Demo user credentials
const DEMO_USERS = {
  demo: {
    id: "demo-user-1",
    username: "demo",
    password: "demo123",
    email: "demo@example.com",
    firstName: "Demo",
    lastName: "User", 
    profileImageUrl: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&auto=format&fit=crop&w=150&h=150",
    balance: "1000000",
    isAdmin: false,
  },
  admin: {
    id: "admin-user-1", 
    username: "admin",
    password: "admin123",
    email: "admin@example.com",
    firstName: "Admin",
    lastName: "User",
    profileImageUrl: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=150&h=150",
    balance: "5000000",
    isAdmin: true,
  }
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Demo login endpoint
  app.post('/api/auth/demo-login', async (req, res) => {
    try {
      const { username, password } = req.body;

      // Check if credentials match demo users
      const user = Object.values(DEMO_USERS).find(u => u.username === username && u.password === password);

      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Create a demo session by storing user info in session
      (req.session as any).demoUser = user;

      // Ensure demo user exists in storage for backend compatibility
      try {
        await storage.upsertUser({
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          profileImageUrl: user.profileImageUrl,
          balance: user.balance,
          isAdmin: user.isAdmin,
        });
      } catch (storageError) {
        console.error("Error upserting demo user to storage:", storageError);
        // Continue even if storage fails - demo might work without DB
      }

      res.json({ 
        message: "Login successful",
        user: {
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          profileImageUrl: user.profileImageUrl,
          balance: user.balance,
          isAdmin: user.isAdmin,
        }
      });
    } catch (error) {
      console.error("Error during demo login:", error);
      res.status(500).json({ message: "Login failed" });
    }
  });

  // Demo logout endpoint
  app.post('/api/auth/demo-logout', async (req, res) => {
    try {
      delete (req.session as any).demoUser;
      res.json({ message: "Logout successful" });
    } catch (error) {
      console.error("Error during demo logout:", error);
      res.status(500).json({ message: "Logout failed" });
    }
  });

  // Auth routes - Modified to support both demo and real auth
  app.get('/api/auth/user', async (req: any, res) => {
    try {
      // Check if user is using demo authentication
      if ((req.session as any)?.demoUser) {
        const demoUser = (req.session as any).demoUser;
        return res.json({
          id: demoUser.id,
          email: demoUser.email,
          firstName: demoUser.firstName,
          lastName: demoUser.lastName,
          profileImageUrl: demoUser.profileImageUrl,
          balance: demoUser.balance,
          isAdmin: demoUser.isAdmin,
          createdAt: new Date(),
          updatedAt: new Date(),
        });
      }

      // Original authentication logic
      if (!req.isAuthenticated() || !req.user?.claims?.sub) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Categories
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  app.post('/api/categories', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const categoryData = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(categoryData);
      res.status(201).json(category);
    } catch (error) {
      console.error("Error creating category:", error);
      res.status(500).json({ message: "Failed to create category" });
    }
  });

  // Tools
  app.get('/api/tools', async (req, res) => {
    try {
      const tools = await storage.getTools();
      res.json(tools);
    } catch (error) {
      console.error("Error fetching tools:", error);
      res.status(500).json({ message: "Failed to fetch tools" });
    }
  });

  app.get('/api/tools/:id', async (req, res) => {
    try {
      const tool = await storage.getToolById(req.params.id);
      if (!tool) {
        return res.status(404).json({ message: "Tool not found" });
      }

      // Increment view count
      await storage.incrementToolViews(req.params.id);

      res.json(tool);
    } catch (error) {
      console.error("Error fetching tool:", error);
      res.status(500).json({ message: "Failed to fetch tool" });
    }
  });

  app.post('/api/tools', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const toolData = insertToolSchema.parse(req.body);
      const tool = await storage.createTool(toolData);
      res.status(201).json(tool);
    } catch (error) {
      console.error("Error creating tool:", error);
      res.status(500).json({ message: "Failed to create tool" });
    }
  });

  app.put('/api/tools/:id', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const toolData = insertToolSchema.partial().parse(req.body);
      const tool = await storage.updateTool(req.params.id, toolData);
      res.json(tool);
    } catch (error) {
      console.error("Error updating tool:", error);
      res.status(500).json({ message: "Failed to update tool" });
    }
  });

  app.delete('/api/tools/:id', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      await storage.deleteTool(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting tool:", error);
      res.status(500).json({ message: "Failed to delete tool" });
    }
  });

  // Purchases
  app.get('/api/purchases', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const purchases = await storage.getUserPurchases(userId);
      res.json(purchases);
    } catch (error) {
      console.error("Error fetching purchases:", error);
      res.status(500).json({ message: "Failed to fetch purchases" });
    }
  });

  app.post('/api/purchases', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { toolId, discountCodeId } = req.body;
      const tool = await storage.getToolById(toolId);
      if (!tool) {
        return res.status(404).json({ message: "Tool not found" });
      }

      let discountAmount = "0";
      let finalPrice = tool.price;

      // Apply discount if provided
      if (discountCodeId) {
        const discountCode = await storage.getDiscountCodeByCode(discountCodeId);
        if (discountCode) {
          if (discountCode.discountType === 'percentage') {
            discountAmount = String(Math.floor(Number(tool.price) * Number(discountCode.discountValue) / 100));
          } else {
            discountAmount = String(Number(discountCode.discountValue) * 100); // Convert to cents
          }
          finalPrice = String(Number(tool.price) - Number(discountAmount));
          await storage.useDiscountCode(discountCode.id);
        }
      }

      // Check if user has enough balance
      if (Number(user.balance) < Number(finalPrice)) {
        return res.status(400).json({ message: "Insufficient balance" });
      }

      // Create purchase with 30 days expiry
      const expiresAt = new Date();
      expiresAt.setDate(expiresAt.getDate() + 30);

      const purchase = await storage.createPurchase({
        userId,
        toolId,
        price: tool.price,
        discountAmount,
        finalPrice,
        discountCodeId: discountCodeId || null,
        expiresAt,
        isActive: true,
      });

      // Deduct from user balance
      await storage.updateUserBalance(userId, `-${finalPrice}`);

      // Log purchase payment
      await storage.createPayment({
        userId,
        amount: `-${finalPrice}`,
        type: "purchase",
        status: "completed",
        description: `Purchased ${tool.name}`,
      });

      res.status(201).json(purchase);
    } catch (error) {
      console.error("Error creating purchase:", error);
      res.status(500).json({ message: "Failed to create purchase" });
    }
  });

  // Update purchase key
  app.put('/api/purchases/:id/key', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { newKey } = req.body;

      // Verify purchase belongs to user
      const purchases = await storage.getUserPurchases(userId);
      const purchase = purchases.find(p => p.id === req.params.id);

      if (!purchase) {
        return res.status(404).json({ message: "Purchase not found" });
      }

      const updatedPurchase = await storage.updatePurchaseKey(req.params.id, newKey);
      res.json(updatedPurchase);
    } catch (error) {
      console.error("Error updating purchase key:", error);
      res.status(500).json({ message: "Failed to update purchase key" });
    }
  });

  // Discount codes
  app.get('/api/discount-codes', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const discountCodes = await storage.getDiscountCodes();
      res.json(discountCodes);
    } catch (error) {
      console.error("Error fetching discount codes:", error);
      res.status(500).json({ message: "Failed to fetch discount codes" });
    }
  });

  app.post('/api/discount-codes/validate', async (req, res) => {
    try {
      const { code } = req.body;
      const discountCode = await storage.getDiscountCodeByCode(code);

      if (!discountCode) {
        return res.status(404).json({ message: "Invalid discount code" });
      }

      // Check if expired
      if (discountCode.expiresAt && discountCode.expiresAt < new Date()) {
        return res.status(400).json({ message: "Discount code has expired" });
      }

      // Check usage limit
      if (discountCode.usageLimit && (discountCode.usageCount || 0) >= discountCode.usageLimit) {
        return res.status(400).json({ message: "Discount code usage limit exceeded" });
      }

      res.json(discountCode);
    } catch (error) {
      console.error("Error validating discount code:", error);
      res.status(500).json({ message: "Failed to validate discount code" });
    }
  });

  app.post('/api/discount-codes', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const discountCodeData = insertDiscountCodeSchema.parse(req.body);
      const discountCode = await storage.createDiscountCode(discountCodeData);
      res.status(201).json(discountCode);
    } catch (error) {
      console.error("Error creating discount code:", error);
      res.status(500).json({ message: "Failed to create discount code" });
    }
  });

  // Payments & Balance
  app.get('/api/payments', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const payments = await storage.getUserPayments(userId);
      res.json(payments);
    } catch (error) {
      console.error("Error fetching payments:", error);
      res.status(500).json({ message: "Failed to fetch payments" });
    }
  });

  app.post('/api/deposit', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { amount, description } = req.body;

      if (!amount || Number(amount) <= 0) {
        return res.status(400).json({ message: "Invalid amount" });
      }

      // Create payment record
      await storage.createPayment({
        userId,
        amount: String(amount),
        type: "deposit",
        status: "completed",
        description: description || "Balance deposit",
      });

      // Update user balance
      const user = await storage.updateUserBalance(userId, String(amount));
      res.json({ balance: user.balance });
    } catch (error) {
      console.error("Error processing deposit:", error);
      res.status(500).json({ message: "Failed to process deposit" });
    }
  });

  // Key validation API
  app.post('/api/validate-key', async (req, res) => {
    try {
      const { key } = req.body;
      const ipAddress = req.ip || req.connection.remoteAddress;
      const userAgent = req.get('User-Agent') || '';

      if (!key) {
        await storage.logKeyValidation({
          keyValue: '',
          isValid: false,
          ipAddress: ipAddress || null,
          userAgent: userAgent || null,
        });
        return res.json({ valid: false });
      }

      const purchase = await storage.getPurchaseByKey(key);

      if (!purchase) {
        await storage.logKeyValidation({
          keyValue: key,
          isValid: false,
          ipAddress: ipAddress || null,
          userAgent: userAgent || null,
        });
        return res.json({ valid: false });
      }

      // Log successful validation
      await storage.logKeyValidation({
        keyValue: key,
        userId: purchase.user.id,
        toolId: purchase.tool.id,
        isValid: true,
        ipAddress: ipAddress || null,
        userAgent: userAgent || null,
      });

      res.json({
        valid: true,
        userName: `${purchase.user.firstName} ${purchase.user.lastName}`.trim() || purchase.user.email,
        expiresAt: purchase.expiresAt,
        toolName: purchase.tool.name,
      });
    } catch (error) {
      console.error("Error validating key:", error);
      res.status(500).json({ message: "Failed to validate key" });
    }
  });

  // Admin endpoints
  app.get('/api/admin/users', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const users = await storage.getUsersWithStats();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.get('/api/admin/stats', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const systemStats = await storage.getSystemStats();
      const keyValidationStats = await storage.getKeyValidationStats();

      res.json({
        ...systemStats,
        keyValidation: keyValidationStats,
      });
    } catch (error) {
      console.error("Error fetching admin stats:", error);
      res.status(500).json({ message: "Failed to fetch admin stats" });
    }
  });

  app.get('/api/admin/key-validations', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user?.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const limit = parseInt(req.query.limit as string) || 50;
      const validations = await storage.getRecentKeyValidations(limit);
      res.json(validations);
    } catch (error) {
      console.error("Error fetching key validations:", error);
      res.status(500).json({ message: "Failed to fetch key validations" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  tools,
  categories,
  purchases,
  discountCodes,
  payments,
  keyValidations,
  type User,
  type UpsertUser,
  type Tool,
  type InsertTool,
  type Category,
  type InsertCategory,
  type Purchase,
  type InsertPurchase,
  type DiscountCode,
  type InsertDiscountCode,
  type Payment,
  type InsertPayment,
  type KeyValidation,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql, gte, count } from "drizzle-orm";
import { randomUUID } from "crypto";
import { mockCategories, mockTools, mockUser, mockAdminUser, mockPayments, mockPurchases } from "../client/src/lib/mockData";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserBalance(userId: string, amount: string): Promise<User>;

  // Category operations
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Tool operations
  getTools(): Promise<(Tool & { category?: Category })[]>;
  getToolById(id: string): Promise<(Tool & { category?: Category }) | undefined>;
  createTool(tool: InsertTool): Promise<Tool>;
  updateTool(id: string, tool: Partial<InsertTool>): Promise<Tool>;
  deleteTool(id: string): Promise<void>;
  incrementToolViews(id: string): Promise<void>;

  // Purchase operations
  getUserPurchases(userId: string): Promise<(Purchase & { tool: Tool })[]>;
  createPurchase(purchase: InsertPurchase): Promise<Purchase>;
  getPurchaseByKey(keyValue: string): Promise<(Purchase & { user: User; tool: Tool }) | undefined>;
  updatePurchaseKey(purchaseId: string, newKey: string): Promise<Purchase>;

  // Discount code operations
  getDiscountCodes(): Promise<DiscountCode[]>;
  getDiscountCodeByCode(code: string): Promise<DiscountCode | undefined>;
  createDiscountCode(discountCode: InsertDiscountCode): Promise<DiscountCode>;
  useDiscountCode(id: string): Promise<DiscountCode>;

  // Payment operations
  getUserPayments(userId: string): Promise<Payment[]>;
  createPayment(payment: InsertPayment): Promise<Payment>;

  // Key validation operations
  logKeyValidation(validation: Omit<KeyValidation, 'id' | 'createdAt'>): Promise<KeyValidation>;
  getKeyValidationStats(): Promise<{ totalToday: number; successToday: number; failedToday: number }>;
  getRecentKeyValidations(limit?: number): Promise<(KeyValidation & { user?: User; tool?: Tool })[]>;

  // Admin operations
  getUsersWithStats(): Promise<(User & { purchaseCount: number; totalSpent: string })[]>;
  getSystemStats(): Promise<{
    totalUsers: number;
    totalTools: number;
    monthlyRevenue: string;
    totalRevenue: string;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserBalance(userId: string, amount: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ 
        balance: sql`${users.balance} + ${amount}`,
        updatedAt: new Date()
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories).orderBy(categories.name);
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  // Tool operations
  async getTools(): Promise<(Tool & { category?: Category })[]> {
    return await db
      .select()
      .from(tools)
      .leftJoin(categories, eq(tools.categoryId, categories.id))
      .where(eq(tools.isActive, true))
      .orderBy(desc(tools.createdAt));
  }

  async getToolById(id: string): Promise<(Tool & { category?: Category }) | undefined> {
    const [result] = await db
      .select()
      .from(tools)
      .leftJoin(categories, eq(tools.categoryId, categories.id))
      .where(and(eq(tools.id, id), eq(tools.isActive, true)));

    if (!result) return undefined;

    return {
      ...result.tools,
      category: result.categories || undefined,
    };
  }

  async createTool(tool: InsertTool): Promise<Tool> {
    const [newTool] = await db.insert(tools).values(tool).returning();
    return newTool;
  }

  async updateTool(id: string, tool: Partial<InsertTool>): Promise<Tool> {
    const [updatedTool] = await db
      .update(tools)
      .set({ ...tool, updatedAt: new Date() })
      .where(eq(tools.id, id))
      .returning();
    return updatedTool;
  }

  async deleteTool(id: string): Promise<void> {
    await db.update(tools)
      .set({ isActive: false, updatedAt: new Date() })
      .where(eq(tools.id, id));
  }

  async incrementToolViews(id: string): Promise<void> {
    await db
      .update(tools)
      .set({ views: sql`${tools.views} + 1` })
      .where(eq(tools.id, id));
  }

  // Purchase operations
  async getUserPurchases(userId: string): Promise<(Purchase & { tool: Tool })[]> {
    return await db
      .select()
      .from(purchases)
      .innerJoin(tools, eq(purchases.toolId, tools.id))
      .where(and(eq(purchases.userId, userId), eq(purchases.isActive, true)))
      .orderBy(desc(purchases.createdAt));
  }

  async createPurchase(purchase: InsertPurchase): Promise<Purchase> {
    const keyValue = randomUUID().replace(/-/g, '').substring(0, 16).toUpperCase();

    const [newPurchase] = await db
      .insert(purchases)
      .values({ ...purchase, keyValue })
      .returning();

    return newPurchase;
  }

  async getPurchaseByKey(keyValue: string): Promise<(Purchase & { user: User; tool: Tool }) | undefined> {
    const [result] = await db
      .select()
      .from(purchases)
      .innerJoin(users, eq(purchases.userId, users.id))
      .innerJoin(tools, eq(purchases.toolId, tools.id))
      .where(and(
        eq(purchases.keyValue, keyValue), 
        eq(purchases.isActive, true),
        gte(purchases.expiresAt, new Date())
      ));

    if (!result) return undefined;

    return {
      ...result.purchases,
      user: result.users,
      tool: result.tools,
    };
  }

  async updatePurchaseKey(purchaseId: string, newKey: string): Promise<Purchase> {
    const [updatedPurchase] = await db
      .update(purchases)
      .set({ keyValue: newKey })
      .where(eq(purchases.id, purchaseId))
      .returning();
    return updatedPurchase;
  }

  // Discount code operations
  async getDiscountCodes(): Promise<DiscountCode[]> {
    return await db.select().from(discountCodes).orderBy(desc(discountCodes.createdAt));
  }

  async getDiscountCodeByCode(code: string): Promise<DiscountCode | undefined> {
    const [discountCode] = await db
      .select()
      .from(discountCodes)
      .where(and(
        eq(discountCodes.code, code), 
        eq(discountCodes.isActive, true)
      ));
    return discountCode;
  }

  async createDiscountCode(discountCode: InsertDiscountCode): Promise<DiscountCode> {
    const [newDiscountCode] = await db
      .insert(discountCodes)
      .values(discountCode)
      .returning();
    return newDiscountCode;
  }

  async useDiscountCode(id: string): Promise<DiscountCode> {
    const [updatedCode] = await db
      .update(discountCodes)
      .set({ usageCount: sql`${discountCodes.usageCount} + 1` })
      .where(eq(discountCodes.id, id))
      .returning();
    return updatedCode;
  }

  // Payment operations
  async getUserPayments(userId: string): Promise<Payment[]> {
    return await db
      .select()
      .from(payments)
      .where(eq(payments.userId, userId))
      .orderBy(desc(payments.createdAt));
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const [newPayment] = await db.insert(payments).values(payment).returning();
    return newPayment;
  }

  // Key validation operations
  async logKeyValidation(validation: Omit<KeyValidation, 'id' | 'createdAt'>): Promise<KeyValidation> {
    const [newValidation] = await db
      .insert(keyValidations)
      .values(validation)
      .returning();
    return newValidation;
  }

  async getKeyValidationStats(): Promise<{ totalToday: number; successToday: number; failedToday: number }> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const [totalResult] = await db
      .select({ count: count() })
      .from(keyValidations)
      .where(gte(keyValidations.createdAt, today));

    const [successResult] = await db
      .select({ count: count() })
      .from(keyValidations)
      .where(and(
        gte(keyValidations.createdAt, today),
        eq(keyValidations.isValid, true)
      ));

    const totalToday = totalResult?.count || 0;
    const successToday = successResult?.count || 0;

    return {
      totalToday,
      successToday,
      failedToday: totalToday - successToday,
    };
  }

  async getRecentKeyValidations(limit = 10): Promise<(KeyValidation & { user?: User; tool?: Tool })[]> {
    return await db
      .select()
      .from(keyValidations)
      .leftJoin(users, eq(keyValidations.userId, users.id))
      .leftJoin(tools, eq(keyValidations.toolId, tools.id))
      .orderBy(desc(keyValidations.createdAt))
      .limit(limit);
  }

  // Admin operations
  async getUsersWithStats(): Promise<(User & { purchaseCount: number; totalSpent: string })[]> {
    return await db
      .select({
        ...users,
        purchaseCount: sql<number>`COALESCE(COUNT(${purchases.id}), 0)`,
        totalSpent: sql<string>`COALESCE(SUM(${purchases.finalPrice}), 0)`,
      })
      .from(users)
      .leftJoin(purchases, eq(users.id, purchases.userId))
      .groupBy(users.id)
      .orderBy(desc(users.createdAt));
  }

  async getSystemStats(): Promise<{
    totalUsers: number;
    totalTools: number;
    monthlyRevenue: string;
    totalRevenue: string;
  }> {
    const [userCount] = await db.select({ count: count() }).from(users);
    const [toolCount] = await db.select({ count: count() }).from(tools).where(eq(tools.isActive, true));

    const currentMonth = new Date();
    currentMonth.setDate(1);
    currentMonth.setHours(0, 0, 0, 0);

    const [monthlyRevenueResult] = await db
      .select({ revenue: sql<string>`COALESCE(SUM(${purchases.finalPrice}), 0)` })
      .from(purchases)
      .where(gte(purchases.createdAt, currentMonth));

    const [totalRevenueResult] = await db
      .select({ revenue: sql<string>`COALESCE(SUM(${purchases.finalPrice}), 0)` })
      .from(purchases);

    return {
      totalUsers: userCount?.count || 0,
      totalTools: toolCount?.count || 0,
      monthlyRevenue: monthlyRevenueResult?.revenue || "0",
      totalRevenue: totalRevenueResult?.revenue || "0",
    };
  }
}

export class MemStorage implements IStorage {
  // In-memory storage
  private users: Map<string, User> = new Map();
  private tools: Map<string, Tool & { category?: Category }> = new Map();
  private categories: Map<string, Category> = new Map();
  private purchases: Map<string, Purchase & { user?: User; tool?: Tool }> = new Map();
  private discountCodes: Map<string, DiscountCode> = new Map();
  private payments: Map<string, Payment> = new Map();
  private keyValidations: Map<string, KeyValidation & { user?: User; tool?: Tool }> = new Map();

  constructor() {
    // Initialize mock data
    mockCategories.forEach(cat => this.categories.set(cat.id, cat));
    mockTools.forEach(tool => this.tools.set(tool.id, tool));
    this.users.set(mockUser.id, mockUser);
    this.users.set(mockAdminUser.id, mockAdminUser);
    mockPayments.forEach(payment => this.payments.set(payment.id, payment));
    mockPurchases.forEach(purchase => this.purchases.set(purchase.id, purchase));
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = {
      id,
      ...userData,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  async updateUserBalance(userId: string, amount: string): Promise<User> {
    const user = this.users.get(userId);
    if (!user) throw new Error("User not found");

    const currentBalance = Number(user.balance) || 0;
    const amountNum = Number(amount);
    const newBalance = currentBalance + amountNum;

    const updatedUser = { ...user, balance: String(newBalance), updatedAt: new Date() };
    this.users.set(userId, updatedUser);
    return updatedUser;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return Array.from(this.categories.values());
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const id = randomUUID();
    const newCategory: Category = { id, ...category, createdAt: new Date() };
    this.categories.set(id, newCategory);
    return newCategory;
  }

  // Tool operations
  async getTools(): Promise<(Tool & { category?: Category })[]> {
    return Array.from(this.tools.values());
  }

  async getToolById(id: string): Promise<(Tool & { category?: Category }) | undefined> {
    return this.tools.get(id);
  }

  async createTool(tool: InsertTool): Promise<Tool> {
    const id = randomUUID();
    const newTool: Tool = {
      id,
      ...tool,
      views: 0,
      rating: "0.0",
      reviewCount: 0,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.tools.set(id, newTool);
    return newTool;
  }

  async updateTool(id: string, toolData: Partial<InsertTool>): Promise<Tool> {
    const tool = this.tools.get(id);
    if (!tool) throw new Error("Tool not found");

    const updatedTool = { ...tool, ...toolData, updatedAt: new Date() };
    this.tools.set(id, updatedTool);
    return updatedTool;
  }

  async deleteTool(id: string): Promise<void> {
    this.tools.delete(id);
  }

  async incrementToolViews(id: string): Promise<void> {
    const tool = this.tools.get(id);
    if (tool) {
      const updatedTool = { ...tool, views: (tool.views || 0) + 1 };
      this.tools.set(id, updatedTool);
    }
  }

  // Purchase operations
  async getUserPurchases(userId: string): Promise<(Purchase & { tool: Tool })[]> {
    return Array.from(this.purchases.values())
      .filter(p => p.userId === userId)
      .map(p => ({ ...p, tool: p.tool! }));
  }

  async createPurchase(purchase: InsertPurchase): Promise<Purchase> {
    const id = randomUUID();
    const keyValue = randomUUID().replace(/-/g, '').substring(0, 16).toUpperCase();
    const newPurchase: Purchase = {
      id,
      ...purchase,
      keyValue,
      createdAt: new Date(),
    };
    this.purchases.set(id, newPurchase);
    return newPurchase;
  }

  async getPurchaseByKey(keyValue: string): Promise<(Purchase & { user: User; tool: Tool }) | undefined> {
    const purchase = Array.from(this.purchases.values())
      .find(p => p.keyValue === keyValue && p.isActive && p.expiresAt > new Date());

    if (!purchase) return undefined;

    const user = this.users.get(purchase.userId);
    const tool = this.tools.get(purchase.toolId);

    if (!user || !tool) return undefined;

    return { ...purchase, user, tool };
  }

  async updatePurchaseKey(purchaseId: string, newKey: string): Promise<Purchase> {
    const purchase = this.purchases.get(purchaseId);
    if (!purchase) throw new Error("Purchase not found");

    const updatedPurchase = { ...purchase, keyValue: newKey };
    this.purchases.set(purchaseId, updatedPurchase);
    return updatedPurchase;
  }

  // Discount code operations
  async getDiscountCodes(): Promise<DiscountCode[]> {
    return Array.from(this.discountCodes.values());
  }

  async getDiscountCodeByCode(code: string): Promise<DiscountCode | undefined> {
    return Array.from(this.discountCodes.values())
      .find(dc => dc.code === code && dc.isActive);
  }

  async createDiscountCode(discountCode: InsertDiscountCode): Promise<DiscountCode> {
    const id = randomUUID();
    const newDiscountCode: DiscountCode = {
      id,
      ...discountCode,
      usageCount: 0,
      createdAt: new Date(),
    };
    this.discountCodes.set(id, newDiscountCode);
    return newDiscountCode;
  }

  async useDiscountCode(id: string): Promise<DiscountCode> {
    const discountCode = this.discountCodes.get(id);
    if (!discountCode) throw new Error("Discount code not found");

    const updatedCode = { ...discountCode, usageCount: discountCode.usageCount + 1 };
    this.discountCodes.set(id, updatedCode);
    return updatedCode;
  }

  // Payment operations
  async getUserPayments(userId: string): Promise<Payment[]> {
    return Array.from(this.payments.values())
      .filter(p => p.userId === userId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const id = randomUUID();
    const newPayment: Payment = {
      id,
      ...payment,
      createdAt: new Date(),
    };
    this.payments.set(id, newPayment);
    return newPayment;
  }

  // Key validation operations
  async logKeyValidation(validation: Omit<KeyValidation, 'id' | 'createdAt'>): Promise<KeyValidation> {
    const id = randomUUID();
    const newValidation: KeyValidation = {
      id,
      ...validation,
      createdAt: new Date(),
    };
    this.keyValidations.set(id, newValidation);
    return newValidation;
  }

  async getKeyValidationStats(): Promise<{ totalToday: number; successToday: number; failedToday: number }> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const todayValidations = Array.from(this.keyValidations.values())
      .filter(v => v.createdAt >= today);

    const totalToday = todayValidations.length;
    const successToday = todayValidations.filter(v => v.isValid).length;

    return {
      totalToday,
      successToday,
      failedToday: totalToday - successToday,
    };
  }

  async getRecentKeyValidations(limit = 10): Promise<(KeyValidation & { user?: User; tool?: Tool })[]> {
    return Array.from(this.keyValidations.values())
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(0, limit);
  }

  // Admin operations
  async getUsersWithStats(): Promise<(User & { purchaseCount: number; totalSpent: string })[]> {
    return Array.from(this.users.values()).map(user => {
      const userPurchases = Array.from(this.purchases.values())
        .filter(p => p.userId === user.id);

      const purchaseCount = userPurchases.length;
      const totalSpent = userPurchases
        .reduce((sum, p) => sum + Number(p.finalPrice), 0)
        .toString();

      return { ...user, purchaseCount, totalSpent };
    });
  }

  async getSystemStats(): Promise<{
    totalUsers: number;
    totalTools: number;
    monthlyRevenue: string;
    totalRevenue: string;
  }> {
    const totalUsers = this.users.size;
    const totalTools = Array.from(this.tools.values()).filter(t => t.isActive).length;

    const currentMonth = new Date();
    currentMonth.setDate(1);
    currentMonth.setHours(0, 0, 0, 0);

    const allPurchases = Array.from(this.purchases.values());
    const monthlyRevenue = allPurchases
      .filter(p => p.createdAt >= currentMonth)
      .reduce((sum, p) => sum + Number(p.finalPrice), 0)
      .toString();

    const totalRevenue = allPurchases
      .reduce((sum, p) => sum + Number(p.finalPrice), 0)
      .toString();

    return {
      totalUsers,
      totalTools,
      monthlyRevenue,
      totalRevenue,
    };
  }
}

export const storage = new MemStorage();

