import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { Layout } from "@/components/Layout";
import { motion } from "framer-motion";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  ShoppingCart, 
  CreditCard, 
  Tag, 
  ArrowLeft,
  CheckCircle,
  AlertCircle,
  Wallet
} from "lucide-react";
import type { Tool, DiscountCode } from "@shared/schema";

export default function Checkout() {
  const [, setLocation] = useLocation();
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [selectedToolId, setSelectedToolId] = useState<string>("");
  const [discountCode, setDiscountCode] = useState("");
  const [appliedDiscount, setAppliedDiscount] = useState<DiscountCode | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);

  // Get tool ID from URL params or localStorage
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const toolId = urlParams.get('tool');
    if (toolId) {
      setSelectedToolId(toolId);
    } else {
      // If no tool specified, redirect back to tools page
      setLocation('/tools');
    }
  }, [setLocation]);

  // Redirect if not authenticated
  useEffect(() => {
    if (!isAuthenticated) {
      toast({
        title: "Cần đăng nhập",
        description: "Vui lòng đăng nhập để tiếp tục mua hàng",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [isAuthenticated, toast]);

  const { data: tool, isLoading: toolLoading } = useQuery<Tool>({
    queryKey: ["/api/tools", selectedToolId],
    enabled: !!selectedToolId,
  });

  const validateDiscountMutation = useMutation({
    mutationFn: async (code: string) => {
      const response = await apiRequest("POST", "/api/discount-codes/validate", { code });
      return await response.json();
    },
    onSuccess: (data) => {
      setAppliedDiscount(data);
      toast({
        title: "Mã giảm giá hợp lệ",
        description: `Giảm ${data.discountType === 'percentage' ? data.discountValue + '%' : Number(data.discountValue).toLocaleString('vi-VN') + '₫'}`,
      });
    },
    onError: (error) => {
      setAppliedDiscount(null);
      toast({
        title: "Mã giảm giá không hợp lệ",
        description: error.message || "Mã giảm giá không tồn tại hoặc đã hết hạn",
        variant: "destructive",
      });
    },
  });

  const purchaseMutation = useMutation({
    mutationFn: async ({ toolId, discountCodeId }: { toolId: string; discountCodeId?: string }) => {
      setIsProcessing(true);
      return await apiRequest("POST", "/api/purchases", { toolId, discountCodeId });
    },
    onSuccess: () => {
      toast({
        title: "Mua thành công!",
        description: "Công cụ đã được thêm vào danh sách đã mua",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/purchases"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });

      // Redirect to purchased tools page
      setTimeout(() => {
        setLocation('/purchased-tools');
      }, 1500);
    },
    onError: (error) => {
      setIsProcessing(false);

      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Lỗi thanh toán",
        description: error.message || "Không thể hoàn tất giao dịch",
        variant: "destructive",
      });
    },
  });

  const handleApplyDiscount = () => {
    if (discountCode.trim()) {
      validateDiscountMutation.mutate(discountCode.trim());
    }
  };

  const handleRemoveDiscount = () => {
    setAppliedDiscount(null);
    setDiscountCode("");
  };

  const handleCompletePurchase = () => {
    if (!tool) return;

    purchaseMutation.mutate({
      toolId: tool.id,
      discountCodeId: appliedDiscount?.code,
    });
  };

  // Calculate pricing
  const originalPrice = tool ? Number(tool.price) : 0;
  let discountAmount = 0;

  if (appliedDiscount && tool) {
    if (appliedDiscount.discountType === 'percentage') {
      discountAmount = Math.floor(originalPrice * Number(appliedDiscount.discountValue) / 100);
    } else {
      discountAmount = Number(appliedDiscount.discountValue) * 100; // Convert to cents
    }
  }

  const finalPrice = originalPrice - discountAmount;
  const hasInsufficientBalance = user ? Number(user.balance) < finalPrice : true;

  if (!isAuthenticated) {
    return (
      <Layout>
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <AlertCircle className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
            <h1 className="text-2xl font-bold mb-2">Cần đăng nhập</h1>
            <p className="text-muted-foreground mb-4">Vui lòng đăng nhập để tiếp tục</p>
            <Button onClick={() => window.location.href = "/api/login"} data-testid="button-login-checkout">
              Đăng nhập
            </Button>
          </div>
        </div>
      </Layout>
    );
  }

  if (toolLoading) {
    return (
      <Layout>
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="max-w-4xl mx-auto">
            <div className="animate-pulse space-y-6">
              <div className="h-8 bg-muted rounded w-32"></div>
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                <div className="lg:col-span-2">
                  <div className="h-64 bg-muted rounded-lg"></div>
                </div>
                <div>
                  <div className="h-96 bg-muted rounded-lg"></div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Layout>
    );
  }

  if (!tool) {
    return (
      <Layout>
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center py-16">
            <AlertCircle className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
            <h1 className="text-2xl font-bold mb-2">Không tìm thấy công cụ</h1>
            <p className="text-muted-foreground mb-6">Công cụ bạn muốn mua không tồn tại hoặc đã bị xóa.</p>
            <Link href="/tools">
              <Button data-testid="button-back-to-tools">
                <ArrowLeft className="mr-2 h-4 w-4" />
                Quay lại danh sách
              </Button>
            </Link>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="min-h-screen bg-background">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="max-w-4xl mx-auto"
          >
            {/* Back Button */}
            <Link href="/tools">
              <Button variant="ghost" className="mb-8" data-testid="button-back-to-tools">
                <ArrowLeft className="mr-2 h-4 w-4" />
                Quay lại danh sách
              </Button>
            </Link>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              {/* Product Details */}
              <motion.div
                className="lg:col-span-2"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.2 }}
              >
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <ShoppingCart className="mr-2 h-5 w-5" />
                      Chi tiết đơn hàng
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-6">
                    {/* Product Info */}
                    <div className="flex items-center space-x-4">
                      <img 
                        src={tool.imageUrl || "https://images.unsplash.com/photo-1460925895917-afdab827c52f?ixlib=rb-4.0.3&auto=format&fit=crop&w=200&h=200"} 
                        alt={tool.name}
                        className="w-20 h-20 object-cover rounded-lg"
                      />
                      <div className="flex-1">
                        <h3 className="text-xl font-bold" data-testid="text-checkout-tool-name">{tool.name}</h3>
                        <p className="text-muted-foreground line-clamp-2" data-testid="text-checkout-tool-description">
                          {tool.description}
                        </p>
                        <div className="flex items-center space-x-2 mt-2">
                          <Badge variant="secondary">Công cụ phần mềm</Badge>
                          <Badge variant="outline">30 ngày sử dụng</Badge>
                        </div>
                      </div>
                    </div>

                    <Separator />

                    {/* Discount Code Section */}
                    <div className="space-y-4">
                      <h4 className="font-semibold">Mã giảm giá</h4>

                      {appliedDiscount ? (
                        <div className="flex items-center justify-between p-4 bg-emerald-50 dark:bg-emerald-950 border border-emerald-200 dark:border-emerald-800 rounded-lg">
                          <div className="flex items-center space-x-2">
                            <CheckCircle className="h-5 w-5 text-emerald-600" />
                            <div>
                              <p className="font-medium text-emerald-800 dark:text-emerald-200">
                                Mã "{appliedDiscount.code}" đã được áp dụng
                              </p>
                              <p className="text-sm text-emerald-600 dark:text-emerald-400">
                                Giảm {appliedDiscount.discountType === 'percentage' 
                                  ? `${appliedDiscount.discountValue}%` 
                                  : `${Number(appliedDiscount.discountValue).toLocaleString('vi-VN')}₫`}
                              </p>
                            </div>
                          </div>
                          <Button 
                            variant="ghost" 
                            size="sm" 
                            onClick={handleRemoveDiscount}
                            data-testid="button-remove-discount"
                          >
                            Xóa
                          </Button>
                        </div>
                      ) : (
                        <div className="flex gap-2">
                          <Input
                            placeholder="Nhập mã giảm giá"
                            value={discountCode}
                            onChange={(e) => setDiscountCode(e.target.value)}
                            data-testid="input-discount-code"
                          />
                          <Button
                            variant="outline"
                            onClick={handleApplyDiscount}
                            disabled={!discountCode.trim() || validateDiscountMutation.isPending}
                            data-testid="button-apply-discount"
                          >
                            <Tag className="w-4 h-4 mr-1" />
                            Áp dụng
                          </Button>
                        </div>
                      )}
                    </div>

                    <Separator />

                    {/* Account Balance */}
                    <div className="space-y-2">
                      <h4 className="font-semibold">Số dư tài khoản</h4>
                      <div className="flex items-center justify-between p-4 bg-muted/50 rounded-lg">
                        <div className="flex items-center space-x-2">
                          <Wallet className="h-5 w-5 text-emerald-600" />
                          <span>Số dư hiện tại:</span>
                        </div>
                        <span className="font-bold text-emerald-600" data-testid="text-checkout-balance">
                          {Number(user?.balance || 0).toLocaleString('vi-VN')}₫
                        </span>
                      </div>

                      {hasInsufficientBalance && (
                        <div className="flex items-center space-x-2 p-3 bg-red-50 dark:bg-red-950 border border-red-200 dark:border-red-800 rounded-lg">
                          <AlertCircle className="h-4 w-4 text-red-600" />
                          <p className="text-sm text-red-800 dark:text-red-200">
                            Số dư không đủ để thực hiện giao dịch này. 
                            <Link href="/deposit" className="underline ml-1">Nạp thêm tiền</Link>
                          </p>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </motion.div>

              {/* Order Summary */}
              <motion.div
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.4 }}
              >
                <Card className="sticky top-24">
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <CreditCard className="mr-2 h-5 w-5" />
                      Tóm tắt đơn hàng
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {/* Pricing Breakdown */}
                    <div className="space-y-3">
                      <div className="flex justify-between">
                        <span>Giá gốc:</span>
                        <span data-testid="text-original-price">{originalPrice.toLocaleString('vi-VN')}₫</span>
                      </div>

                      {appliedDiscount && discountAmount > 0 && (
                        <div className="flex justify-between text-emerald-600">
                          <span>Giảm giá:</span>
                          <span data-testid="text-discount-amount">-{discountAmount.toLocaleString('vi-VN')}₫</span>
                        </div>
                      )}

                      <Separator />

                      <div className="flex justify-between text-lg font-bold">
                        <span>Tổng cộng:</span>
                        <span className="text-primary" data-testid="text-final-price">
                          {finalPrice.toLocaleString('vi-VN')}₫
                        </span>
                      </div>
                    </div>

                    <Separator />

                    {/* Purchase Button */}
                    <div className="space-y-3">
                      <Button 
                        className="w-full" 
                        onClick={handleCompletePurchase}
                        disabled={hasInsufficientBalance || isProcessing || purchaseMutation.isPending}
                        data-testid="button-complete-purchase"
                      >
                        {isProcessing || purchaseMutation.isPending ? (
                          "Đang xử lý..."
                        ) : hasInsufficientBalance ? (
                          "Số dư không đủ"
                        ) : (
                          `Thanh toán ${finalPrice.toLocaleString('vi-VN')}₫`
                        )}
                      </Button>

                      {hasInsufficientBalance && (
                        <Link href="/deposit">
                          <Button variant="outline" className="w-full" data-testid="button-deposit-more">
                            <Wallet className="w-4 h-4 mr-2" />
                            Nạp thêm tiền
                          </Button>
                        </Link>
                      )}
                    </div>

                    <Separator />

                    {/* Purchase Terms */}
                    <div className="text-xs text-muted-foreground space-y-1">
                      <p>• Công cụ sẽ có hiệu lực trong 30 ngày kể từ khi mua</p>
                      <p>• Bạn có thể đổi license key bất cứ lúc nào</p>
                      <p>• Hỗ trợ kỹ thuật 24/7 qua Telegram, Zalo, Email</p>
                      <p>• Không hoàn tiền sau khi mua thành công</p>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            </div>

            {/* Success Modal Effect */}
            {isProcessing && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center"
              >
                <motion.div
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  className="bg-card border border-border rounded-xl p-8 text-center max-w-md mx-4"
                >
                  <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4"
                  ></motion.div>
                  <h3 className="text-xl font-bold mb-2">Đang xử lý thanh toán</h3>
                  <p className="text-muted-foreground">Vui lòng đợi trong giây lát...</p>
                </motion.div>
              </motion.div>
            )}
          </motion.div>
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/Dashboard.tsx.content:
import { Layout } from "@/components/Layout";
import { motion } from "framer-motion";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  CreditCard, 
  ShoppingBag, 
  Calendar, 
  TrendingUp,
  ArrowRight,
  Clock
} from "lucide-react";
import { Link } from "wouter";
import type { Purchase, Tool, Payment } from "@shared/schema";

export default function Dashboard() {
  const { user } = useAuth();

  const { data: purchases, isLoading: purchasesLoading } = useQuery<(Purchase & { tool: Tool })[]>({
    queryKey: ["/api/purchases"],
  });

  const { data: payments, isLoading: paymentsLoading } = useQuery<Payment[]>({
    queryKey: ["/api/payments"],
  });

  const totalSpent = payments?.reduce((sum, payment) => {
    return payment.type === "purchase" ? sum + Math.abs(Number(payment.amount)) : sum;
  }, 0) || 0;

  const recentPurchases = purchases?.slice(0, 3) || [];

  const statsCards = [
    {
      title: "Số dư",
      value: `${Number(user?.balance || 0).toLocaleString('vi-VN')}₫`,
      icon: CreditCard,
      color: "text-emerald-600",
      bgColor: "bg-emerald-100 dark:bg-emerald-900"
    },
    {
      title: "Công cụ đã mua",
      value: purchases?.length || 0,
      icon: ShoppingBag,
      color: "text-blue-600",
      bgColor: "bg-blue-100 dark:bg-blue-900"
    },
    {
      title: "Ngày tham gia",
      value: user?.createdAt ? new Date(user.createdAt).toLocaleDateString('vi-VN') : 'N/A',
      icon: Calendar,
      color: "text-purple-600",
      bgColor: "bg-purple-100 dark:bg-purple-900"
    },
    {
      title: "Đã chi tiêu",
      value: `${totalSpent.toLocaleString('vi-VN')}₫`,
      icon: TrendingUp,
      color: "text-orange-600",
      bgColor: "bg-orange-100 dark:bg-orange-900"
    }
  ];

  return (
    <Layout showSidebar>
      <div className="p-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <div className="mb-8">
            <h1 className="text-3xl font-bold mb-2">Dashboard</h1>
            <p className="text-muted-foreground">Quản lý tài khoản và công cụ của bạn</p>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {statsCards.map((stat, index) => (
              <motion.div
                key={stat.title}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
              >
                <Card className="hover:shadow-lg transition-all duration-200 hover:-translate-y-1">
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm text-muted-foreground">{stat.title}</p>
                        <p className={`text-2xl font-bold ${stat.color}`} data-testid={`text-stat-${stat.title.toLowerCase().replace(/\s+/g, '-')}`}>
                          {stat.value}
                        </p>
                      </div>
                      <div className={`w-12 h-12 ${stat.bgColor} rounded-lg flex items-center justify-center`}>
                        <stat.icon className={`w-6 h-6 ${stat.color}`} />
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            ))}
          </div>

          {/* Recent Purchases */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-xl font-semibold">Công cụ gần đây</CardTitle>
                  <Link href="/purchased-tools">
                    <Button variant="outline" size="sm" data-testid="link-view-all-purchases">
                      Xem tất cả
                      <ArrowRight className="ml-2 h-4 w-4" />
                    </Button>
                  </Link>
                </div>
              </CardHeader>
              <CardContent>
                {purchasesLoading ? (
                  <div className="space-y-4">
                    {[...Array(3)].map((_, i) => (
                      <div key={i} className="flex items-center justify-between p-4 border border-border rounded-lg">
                        <div className="flex items-center space-x-4">
                          <Skeleton className="w-12 h-12 rounded-lg" />
                          <div className="space-y-1">
                            <Skeleton className="h-4 w-32" />
                            <Skeleton className="h-3 w-24" />
                          </div>
                        </div>
                        <div className="text-right space-y-1">
                          <Skeleton className="h-4 w-20" />
                          <Skeleton className="h-3 w-16" />
                        </div>
                      </div>
                    ))}
                  </div>
                ) : recentPurchases.length > 0 ? (
                  <div className="space-y-4">
                    {recentPurchases.map((purchase) => {
                      const daysLeft = Math.ceil((new Date(purchase.expiresAt).getTime() - Date.now()) / (1000 * 60 * 60 * 24));
                      const isExpiringSoon = daysLeft <= 7;
                      const isExpired = daysLeft < 0;

                      return (
                        <div key={purchase.id} className="flex items-center justify-between p-4 border border-border rounded-lg">
                          <div className="flex items-center space-x-4">
                            <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                              <ShoppingBag className="w-6 h-6 text-white" />
                            </div>
                            <div>
                              <p className="font-semibold" data-testid={`text-purchase-tool-name-${purchase.id}`}>
                                {purchase.tool.name}
                              </p>
                              <p className="text-sm text-muted-foreground" data-testid={`text-purchase-date-${purchase.id}`}>
                                Mua ngày {new Date(purchase.createdAt).toLocaleDateString('vi-VN')}
                              </p>
                            </div>
                          </div>
                          <div className="text-right">
                            <p className="font-semibold" data-testid={`text-purchase-price-${purchase.id}`}>
                              {Number(purchase.finalPrice).toLocaleString('vi-VN')}₫
                            </p>
                            <Badge 
                              variant={isExpired ? "destructive" : isExpiringSoon ? "secondary" : "default"}
                              className={isExpired ? "" : isExpiringSoon ? "bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200" : "bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200"}
                            >
                              <Clock className="w-3 h-3 mr-1" />
                              {isExpired ? "Đã hết hạn" : `Còn ${daysLeft} ngày`}
                            </Badge>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <ShoppingBag className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">Chưa có công cụ nào</h3>
                    <p className="text-muted-foreground mb-4">
                      Bắt đầu khám phá và mua các công cụ chất lượng
                    </p>
                    <Link href="/tools">
                      <Button data-testid="button-browse-tools-from-dashboard">
                        Duyệt công cụ
                        <ArrowRight className="ml-2 h-4 w-4" />
                      </Button>
                    </Link>
                  </div>
                )}
              </CardContent>
            </Card>
          </motion.div>
        </motion.div>
      </div>
    </Layout>
  );
}

client/src/pages/Deposit.tsx.content:
import { Layout } from "@/components/Layout";
import { motion } from "framer-motion";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  CreditCard, 
  Wallet, 
  QrCode,
  CheckCircle,
  Clock,
  DollarSign
} from "lucide-react";
import { useState } from "react";
import type { Payment } from "@shared/schema";

export default function Deposit() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [amount, setAmount] = useState("");
  const [description, setDescription] = useState("");

  const { data: payments, isLoading: paymentsLoading } = useQuery<Payment[]>({
    queryKey: ["/api/payments"],
    retry: false,
  });

  const depositMutation = useMutation({
    mutationFn: async ({ amount, description }: { amount: string; description: string }) => {
      const response = await apiRequest("POST", "/api/deposit", { 
        amount: Number(amount) * 100, // Convert to cents
        description 
      });
      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: "Nạp tiền thành công",
        description: "Số dư của bạn đã được cập nhật",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/payments"] });
      setAmount("");
      setDescription("");
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Lỗi",
        description: error.message || "Không thể nạp tiền",
        variant: "destructive",
      });
    },
  });

  const handleDeposit = () => {
    if (!amount || Number(amount) <= 0) {
      toast({
        title: "Lỗi",
        description: "Vui lòng nhập số tiền hợp lệ",
        variant: "destructive",
      });
      return;
    }

    depositMutation.mutate({
      amount,
      description: description || "Nạp tiền vào tài khoản",
    });
  };

  const quickAmounts = [50000, 100000, 200000, 500000, 1000000, 2000000];

  const recentDeposits = payments?.filter(p => p.type === "deposit").slice(0, 5) || [];

  return (
    <Layout showSidebar>
      <div className="p-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <div className="mb-8">
            <h1 className="text-3xl font-bold mb-2">Nạp tiền</h1>
            <p className="text-muted-foreground">Nạp tiền vào tài khoản để mua các công cụ</p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Deposit Form */}
            <motion.div
              className="lg:col-span-2"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <CreditCard className="mr-2 h-5 w-5" />
                    Nạp tiền vào tài khoản
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  {/* Current Balance */}
                  <div className="bg-muted/50 p-4 rounded-lg">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Wallet className="h-5 w-5 text-emerald-600" />
                        <span className="font-medium">Số dư hiện tại:</span>
                      </div>
                      <span className="text-2xl font-bold text-emerald-600" data-testid="text-current-balance">
                        {Number(user?.balance || 0).toLocaleString('vi-VN')}₫
                      </span>
                    </div>
                  </div>

                  {/* Quick Amount Selection */}
                  <div className="space-y-3">
                    <Label>Chọn nhanh số tiền</Label>
                    <div className="grid grid-cols-3 gap-2">
                      {quickAmounts.map((quickAmount) => (
                        <Button
                          key={quickAmount}
                          variant={amount === quickAmount.toString() ? "default" : "outline"}
                          size="sm"
                          onClick={() => setAmount(quickAmount.toString())}
                          data-testid={`button-quick-amount-${quickAmount}`}
                        >
                          {quickAmount.toLocaleString('vi-VN')}₫
                        </Button>
                      ))}
                    </div>
                  </div>

                  {/* Custom Amount */}
                  <div className="space-y-2">
                    <Label htmlFor="amount">Hoặc nhập số tiền tùy chỉnh (₫)</Label>
                    <Input
                      id="amount"
                      type="number"
                      placeholder="Nhập số tiền muốn nạp"
                      value={amount}
                      onChange={(e) => setAmount(e.target.value)}
                      min="10000"
                      step="10000"
                      data-testid="input-custom-amount"
                    />
                    <p className="text-sm text-muted-foreground">
                      Số tiền tối thiểu: 10,000₫
                    </p>
                  </div>

                  {/* Description */}
                  <div className="space-y-2">
                    <Label htmlFor="description">Ghi chú (tùy chọn)</Label>
                    <Textarea
                      id="description"
                      placeholder="Nhập ghi chú cho giao dịch này"
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      rows={3}
                      data-testid="input-description"
                    />
                  </div>

                  <Button 
                    className="w-full" 
                    onClick={handleDeposit}
                    disabled={!amount || Number(amount) <= 0 || depositMutation.isPending}
                    data-testid="button-deposit"
                  >
                    {depositMutation.isPending ? "Đang xử lý..." : `Nạp ${amount ? Number(amount).toLocaleString('vi-VN') + '₫' : 'tiền'}`}
                  </Button>
                </CardContent>
              </Card>

              {/* Payment Instructions */}
              <Card className="mt-6">
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <QrCode className="mr-2 h-5 w-5" />
                    Hướng dẫn nạp tiền
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="bg-blue-50 dark:bg-blue-950 p-4 rounded-lg">
                      <h4 className="font-semibold text-blue-800 dark:text-blue-200 mb-2">Chuyển khoản ngân hàng</h4>
                      <div className="text-sm space-y-1">
                        <p><strong>Ngân hàng:</strong> Vietcombank</p>
                        <p><strong>Số tài khoản:</strong> 1234567890</p>
                        <p><strong>Chủ tài khoản:</strong> CONG TY TOOLMARKET</p>
                        <p><strong>Nội dung:</strong> NAPIEN [email của bạn]</p>
                      </div>
                    </div>

                    <div className="bg-emerald-50 dark:bg-emerald-950 p-4 rounded-lg">
                      <h4 className="font-semibold text-emerald-800 dark:text-emerald-200 mb-2">Ví điện tử MoMo</h4>
                      <div className="text-sm space-y-1">
                        <p><strong>Số điện thoại:</strong> 0901234567</p>
                        <p><strong>Chủ tài khoản:</strong> TOOLMARKET</p>
                        <p><strong>Nội dung:</strong> NAPIEN [email của bạn]</p>
                      </div>
                    </div>

                    <div className="text-sm text-muted-foreground p-4 bg-muted/50 rounded-lg">
                      <p className="font-medium mb-2">Lưu ý quan trọng:</p>
                      <ul className="space-y-1 list-disc list-inside">
                        <li>Vui lòng ghi đúng nội dung chuyển khoản để hệ thống tự động cộng tiền</li>
                        <li>Số dư sẽ được cập nhật trong vòng 5-10 phút sau khi chuyển khoản thành công</li>
                        <li>Nếu có vấn đề, vui lòng liên hệ bộ phận hỗ trợ</li>
                      </ul>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </motion.div>

            {/* Sidebar */}
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
            >
              {/* Account Summary */}
              <Card className="mb-6">
                <CardHeader>
                  <CardTitle>Thông tin tài khoản</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-muted-foreground">Số dư hiện tại</span>
                    <span className="font-bold text-emerald-600" data-testid="text-sidebar-balance">
                      {Number(user?.balance || 0).toLocaleString('vi-VN')}₫
                    </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-muted-foreground">Loại tài khoản</span>
                    <Badge variant={user?.isAdmin ? "default" : "secondary"}>
                      {user?.isAdmin ? "Quản trị viên" : "Thành viên"}
                    </Badge>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-muted-foreground">Ngày tham gia</span>
                    <span className="text-sm font-medium">
                      {user?.createdAt ? new Date(user.createdAt).toLocaleDateString('vi-VN') : 'N/A'}
                    </span>
                  </div>
                </CardContent>
              </Card>

              {/* Recent Deposits */}
              <Card>
                <CardHeader>
                  <CardTitle>Lịch sử nạp tiền gần đây</CardTitle>
                </CardHeader>
                <CardContent>
                  {paymentsLoading ? (
                    <div className="space-y-3">
                      {[...Array(3)].map((_, i) => (
                        <div key={i} className="flex items-center justify-between p-2">
                          <div className="space-y-1">
                            <div className="h-4 bg-muted rounded w-24"></div>
                            <div className="h-3 bg-muted rounded w-16"></div>
                          </div>
                          <div className="h-4 bg-muted rounded w-20"></div>
                        </div>
                      ))}
                    </div>
                  ) : recentDeposits.length > 0 ? (
                    <div className="space-y-3">
                      {recentDeposits.map((payment) => (
                        <div key={payment.id} className="flex items-center justify-between py-2">
                          <div className="flex items-center space-x-2">
                            <div className="w-6 h-6 bg-emerald-100 dark:bg-emerald-900 rounded-full flex items-center justify-center">
                              <DollarSign className="w-3 h-3 text-emerald-600" />
                            </div>
                            <div>
                              <p className="text-sm font-medium">
                                +{Number(payment.amount).toLocaleString('vi-VN')}₫
                              </p>
                              <p className="text-xs text-muted-foreground">
                                {new Date(payment.createdAt).toLocaleDateString('vi-VN')}
                              </p>
                            </div>
                          </div>
                          <Badge 
                            variant={payment.status === 'completed' ? 'default' : payment.status === 'pending' ? 'secondary' : 'destructive'}
                            className="text-xs"
                          >
                            {payment.status === 'completed' ? (
                              <>
                                <CheckCircle className="w-3 h-3 mr-1" />
                                Thành công
                              </>
                            ) : payment.status === 'pending' ? (
                              <>
                                <Clock className="w-3 h-3 mr-1" />
                                Đang xử lý
                              </>
                            ) : (
                              'Thất bại'
                            )}
                          </Badge>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-4">
                      <p className="text-sm text-muted-foreground">Chưa có giao dịch nạp tiền nào</p>
                    </div>
                  )}
                </CardContent>
              </Card>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </Layout>
  );
}

